CREATE TABLE "user" (
	"id" SERIAL PRIMARY KEY,
	"username" varchar(80) NOT NULL UNIQUE,
	"password" varchar(1000) NOT NULL,
	"avatar" TEXT
);



CREATE TABLE "recipes" (
	"id" SERIAL PRIMARY KEY,
	"recipe_name" varchar(100) NOT NULL,
	"fav" BOOLEAN DEFAULT 'false',
	"photo" TEXT,
	"directions" TEXT NOT NULL,
    "servings" INT,
	"meal" varchar(50),
	"user_id" int REFERENCES "user" ON DELETE CASCADE NOT NULL

);



CREATE TABLE "ingredients" (
	"id" SERIAL PRIMARY KEY,
	"quantity" DECIMAL,
	"measure" VARCHAR(100),
	"ingredient" TEXT NOT NULL,
	"need" BOOLEAN NOT NULL DEFAULT 'true',
	"recipe_id" INT REFERENCES "recipes" ON DELETE CASCADE NOT NULL


);



CREATE TABLE "nutrition_info" (
	"id" SERIAL PRIMARY KEY,
	"cal" DECIMAL DEFAULT 0,
	"fat" DECIMAL DEFAULT 0,
	"sat_fat" DECIMAL DEFAULT 0,
	"trans_fat" DECIMAL DEFAULT 0,
	"mono_un_fat" DECIMAL DEFAULT 0,
	"poly_un_fat" DECIMAL DEFAULT 0,
	"carb" DECIMAL DEFAULT 0,
	"fiber" DECIMAL DEFAULT 0,
	"sugar" DECIMAL DEFAULT 0,
	"protein" DECIMAL DEFAULT 0,
	"chol" DECIMAL DEFAULT 0,
	"sodium" DECIMAL DEFAULT 0,
	"calcium" DECIMAL DEFAULT 0,
	"magnesium" DECIMAL DEFAULT 0,
	"potassium" DECIMAL DEFAULT 0,
	"iron" DECIMAL DEFAULT 0,
	"zinc" DECIMAL DEFAULT 0,
	"phosphorus" DECIMAL DEFAULT 0,
	"vit_a" DECIMAL DEFAULT 0,
	"vit_c" DECIMAL DEFAULT 0,
	"vit_b1" DECIMAL DEFAULT 0,
	"vit_b2" DECIMAL DEFAULT 0,
	"vit_b3" DECIMAL DEFAULT 0,
	"vit_b6" DECIMAL DEFAULT 0,
	"vit_b9" DECIMAL DEFAULT 0,
	"vit_b12" DECIMAL DEFAULT 0,
	"vit_d" DECIMAL DEFAULT 0,
	"vit_e" DECIMAL DEFAULT 0,
	"vit_k" DECIMAL DEFAULT 0,
	"recipes_id" INT REFERENCES "recipes" ON DELETE CASCADE NOT NULL
);


CREATE TABLE "week" (
	"id" SERIAL PRIMARY KEY,
	"meal_index" INT NOT NULL,
	"day_index" INT NOT NULL,
	"recipe_id" int REFERENCES "recipes" ON DELETE CASCADE  NOT NULL,
	"user_id" int REFERENCES "user" ON DELETE CASCADE NOT NULL
);



CREATE TABLE "user_settings" (
	"id" SERIAL PRIMARY KEY,
	"nutrient" varchar(50) NOT NULL,
	"limit" int NOT NULL,
	"tracked" BOOLEAN NOT NULL DEFAULT 'false',
	"user_id" int REFERENCES "user" ON DELETE CASCADE NOT NULL
);

